mapper.insert:
insert into RECIPE (ID, M_EMAIL, M_NICKNAME, PROFILE_IMG, TITLE, INFO, FOOD, FOOD_PHOTO, SORT, INGREDIENT, SERVING, COOK_TIME, DIFFICULTY_LEVEL,
ACCESSIBILITY) values(RECIPE_SEQ.nextval, #{mEmail}, #{mNickname}, #{profileImg}, #{title}, #{info}, #{food, jdbcType=VARCHAR}, #{foodPhoto, jdbcType=VARCHAR}, #{sort}, #{ingredient}, #{serving}, #{cookTime}, #{difficultyLevel}, #{accessibility})
select NVL(MAX(ID),0) from RECIPE where M_EMAIL=#{mEmail}

mapper.update:
update DIRECTION set STEP=#{step},
					 DIRECTION=#{direction},
					 ORIGINAL_FILE=#{originalFile},
					 SAVE_FILE=#{saveFile}
where R_ID=#{rId} and STEP=#{step}
